with open("inputs/problem2.txt") as f: print(sum((lambda l: all(1 <= abs(l[i+1] - l[i]) <= 3 for i in range(len(l) - 1)) and (all(l[i+1] >= l[i] for i in range(len(l) - 1)) or all(l[i+1] <= l[i] for i in range(len(l) - 1))))(r) for r in [list(map(int, line.split())) for line in f])); f.seek(0); print(sum((lambda r: (lambda l: all(1 <= abs(l[i+1] - l[i]) <= 3 for i in range(len(l) - 1)) and (all(l[i+1] >= l[i] for i in range(len(l) - 1)) or all(l[i+1] <= l[i] for i in range(len(l) - 1))))(r) or any((lambda l: all(1 <= abs(l[i+1] - l[i]) <= 3 for i in range(len(l) - 1)) and (all(l[i+1] >= l[i] for i in range(len(l) - 1)) or all(l[i+1] <= l[i] for i in range(len(l) - 1))))(r[:i] + r[i+1:]) for i in range(len(r))))(r) for r in [list(map(int, line.split())) for line in f]))